// HTML i√ßeriklerinin bulunduƒüu bir liste
const htmlContents = items.map(item => item.json.data);

// Regex ile <meta property="og:image"> etiketini buluyoruz
const regex = /<meta property="og:image" content="(.*?)"/;

// Sonu√ßlarƒ± saklamak i√ßin bir liste olu≈üturuyoruz
const results = [];

// Her bir HTML i√ßeriƒüi √ºzerinde d√∂ng√º yapƒ±yoruz
htmlContents.forEach(htmlContent => {
  const match = htmlContent.match(regex);
  const imageUrl = match ? match[1] : "G√∂rsel bulunamadƒ±";
  results.push({ json: { imageUrl } });
});

// Sonu√ßlarƒ± d√∂nd√ºr√ºyoruz
return results;

// Gelen veriyi alƒ±n
const inputData = items[0]?.json || {};
const output = inputData?.output || "Mesaj bulunamadƒ±.";

// Slack i√ßin uygun formatta metni d√ºzenleyin
let sanitizedContent = output
  .replace(/(\r\n|\r|\n)/g, '\\n') // Satƒ±r sonlarƒ±nƒ± a√ßƒ±k√ßa `\\n` ile deƒüi≈ütir
  .replace(/"/g, '\\"'); // √áift tƒ±rnaklarƒ± Slack i√ßin ka√ßƒ±≈ü karakteriyle deƒüi≈ütir

// Ba≈ülƒ±klarƒ± koruyarak Slack formatƒ±na uygun hale getir
sanitizedContent = sanitizedContent.replace(
  /(\*üì∞ Original Feed\*|\*üá∫üá∏~>üáπüá∑ Haber\*)/gm,
  (_, heading) => `${heading}:\n\n` // Ba≈ülƒ±ktan sonra iki bo≈ü satƒ±r ekle
);

// √áoklu `\n` karakterlerini birle≈ütir
sanitizedContent = sanitizedContent.replace(/\\n+/g, '\\n');

// HTML entity'leri temizlemek i√ßin bir i≈ülev ekleyin
function decodeHtmlEntities(text) {
  const entities = {
    '&#8217;': "'", // Tek tƒ±rnak
    '&#8230;': '...', // √ú√ß nokta
    '&#8220;': '"', // Sol √ßift tƒ±rnak
    '&#8221;': '"', // Saƒü √ßift tƒ±rnak
    '&amp;': '&', // Ampersand
    '&nbsp;': ' ', // Bo≈üluk
    '&lt;': '<', // K√º√ß√ºkt√ºr i≈üareti
    '&gt;': '>'  // B√ºy√ºkt√ºr i≈üareti
  };
  return text.replace(/&#?\w+;/g, match => entities[match] || match);
}

// HTML √∂zel karakterlerini temizle
sanitizedContent = decodeHtmlEntities(sanitizedContent);

// Slack i√ßin uygun JSON formatƒ±nda d√∂nd√ºr
return [
  {
    json: {
      type: "section",
      text: {
        type: "mrkdwn",
        text: sanitizedContent
      }
    }
  }
];

