const items = $input.all();

function sev(score) {
  if (score === 0) return "NONE";
  if (score <= 3.9) return "ðŸ”µ LOW";
  if (score <= 6.9) return "ðŸŸ¡ MEDIUM";
  if (score <= 8.9) return "ðŸ”´ HIGH";
  if (score <= 10) return "âš« CRITICAL";
  return "Unknown";
}

return items.map(e => {
  const d = e.json;
  const m = (d.cvssScoreRaw || "").match(/(\d+(\.\d+)?)/);
  const score = m ? parseFloat(m[1]) : null;

  return {
    json: {
      cveId: d.cveId,
      references: Array.isArray(d.references) ? d.references : [d.references],
      highestBaseScore: score,
      highestBaseSeverity: score !== null ? sev(score) : "Unknown"
    }
  };
});

