parameters.jsCode:
const store = $getWorkflowStaticData('global');

const nowUtc = new Date();

function toTurkeyISOString(date) {
  const istanbul = new Date(date.getTime() + 3 * 60 * 60 * 1000);
  return istanbul.toISOString().slice(0, 19);
}

if (!store.lastRunUtc) {
  store.lastRunUtc = new Date(nowUtc.getTime() - 7*24*60*60*1000).toISOString();
}

const pubStartDate = toTurkeyISOString(new Date(store.lastRunUtc));
const pubEndDate   = toTurkeyISOString(nowUtc);

store.lastRunUtc = nowUtc.toISOString();

return [{
  json: {
    pubStartDate,
    pubEndDate,
    nowUtc: nowUtc.toISOString()
  }
}];

const inputs = $input.all();
const out = [];

function extractCvss(cve) {
  const m31 = cve.metrics?.cvssMetricV31?.[0]?.cvssData;
  const m30 = cve.metrics?.cvssMetricV30?.[0]?.cvssData;
  const m20 = cve.metrics?.cvssMetricV2?.[0];
  if (m31) return { score: m31.baseScore, severity: cve.metrics.cvssMetricV31[0]?.baseSeverity || 'Unknown' };
  if (m30) return { score: m30.baseScore, severity: cve.metrics.cvssMetricV30[0]?.baseSeverity || 'Unknown' };
  if (m20) return { score: m20.baseScore, severity: m20.baseSeverity || 'Unknown' };
  return { score: null, severity: 'Unknown' };
}

function firstEnDesc(arr) {
  if (!Array.isArray(arr)) return '';
  const en = arr.find(d => d?.lang === 'en');
  return en?.value || arr[0]?.value || '';
}

function weaknessCats(ws) {
  if (!Array.isArray(ws)) return [];
  return ws.map(w => Array.isArray(w.description) ? w.description[0]?.value : undefined).filter(Boolean);
}

for (const item of inputs) {
  const root = (item.json && item.json.body && typeof item.json.body === 'object') ? item.json.body : item.json;
  const vendor = item.json.vendor || root.vendor || 'unknown';

  const vulns = Array.isArray(root.vulnerabilities) ? root.vulnerabilities : [];
  if (!vulns.length) continue; 

  for (const v of vulns) {
    const c = v.cve || v;
    const cveId = c.id;
    const link = `https://nvd.nist.gov/vuln/detail/${cveId}`;
    const pubDate = c.published || c.lastModified || null;
    const desc = firstEnDesc(c.descriptions);
    const cvss = extractCvss(c);
    const cats = weaknessCats(c.weaknesses);

    out.push({
      json: {
        title: `${cveId} - ${desc.slice(0,140)}`,
        link,
        pubDate: pubDate ? new Date(pubDate).toUTCString().replace('GMT', '+0000') : null,
        contentSnippet: desc,
        categories: cats,
        highestBaseScore: cvss.score,
        highestBaseSeverity: cvss.severity,
        cveId,
        vendorHint: (JSON.stringify(c.configurations || '') + ' ' + desc).toLowerCase(),
        vendor
      }
    });
  }
}

return out;

