const pubStartDate = $json.pubStartDate;
const pubEndDate   = $json.pubEndDate;

const base = 'https://services.nvd.nist.gov/rest/json/cves/2.0';

const terms = [
  { vendor: 'f5',       q: '("F5" OR "BIG-IP")' },
  { vendor: 'fortinet', q: '(Fortinet OR FortiOS OR FortiGate)' },
  { vendor: 'papercut', q: 'PaperCut' },
  { vendor: 'trellix',  q: 'Trellix' },
  { vendor: 'vmware',   q: '(VMware OR vCenter OR ESXi)' },
  { vendor: 'n8n',      q: 'n8n' },
  { vendor: 'cisco',    q: 'Cisco' },
  { vendor: 'zabbix',   q: 'Zabbix' },
  { vendor: 'cohesity', q: 'Cohesity' },
  { vendor: 'qnap',     q: 'QNAP' },
];

function enc(s){ return encodeURIComponent(s); }

function urlFor(q) {
  return `${base}?pubStartDate=${enc(pubStartDate)}&pubEndDate=${enc(pubEndDate)}&startIndex=0&resultsPerPage=200&keywordSearch=${enc(q)}`;
}

return terms.map(t => ({ json: { vendor: t.vendor, url: urlFor(t.q) } }));

